package com.sindhu.calculator;

import java.util.Scanner;

public class SimpleCalculator {
	int num1;
	int num2;
	public SimpleCalculator(int num1,int num2) {
		// TODO Auto-generated constructor stub
		this.num1=num1;
		this.num2=num2;
	}
	public int add() {
		
		return num1+num2;
		

	}

	public int subtract() {
		
		return num1 - num2;
		

	}

	public int multiplication() {
		
		return num1 * num2;
		

	}

	public int division() {
		
		return num1 / num2;
		
	
}

	
}
